CREATE TABLE user_table(user_id INTEGER PRIMARY KEY AUTOINCREMENT, user_online_id INTEGER DEFAULT 0, user_role_id INTEGER DEFAULT 0, user_token TEXT, user_name TEXT, user_email TEXT, user_mobile_no TEXT, user_password TEXT, user_profile TEXT, user_permission TEXT, user_status INTEGER, is_synced INTEGER DEFAULT 0, is_delete INTEGER DEFAULT 0, is_agency INTEGER DEFAULT 0, user_added_by INTEGER DEFAULT 0, user_module_ids TEXT, user_permissions_ids TEXT, is_status_synced INTEGER DEFAULT 0, signed_in_user INTEGER DEFAULT 0);
CREATE TABLE user_groups_table(group_id INTEGER PRIMARY KEY AUTOINCREMENT, group_online_id INTEGER DEFAULT 0, group_name TEXT, group_module_list TEXT, group_permission_list TEXT, is_synced INTEGER DEFAULT 0, is_delete INTEGER DEFAULT 0, group_added_by INTEGER DEFAULT 0);